SELECT T1.REST_ID, T1.REST_NAME, T1.FOOD_TYPE, T1.FAVORITES, T1.ADDRESS, round(AVG(REVIEW_SCORE),2) AS SCORE FROM REST_INFO T1, REST_REVIEW T2 WHERE T1.REST_ID=T2.REST_ID and T1.ADDRESS LIKE '서울%' GROUP BY T1.REST_ID, T1.REST_NAME, T1.FOOD_TYPE, T1.FAVORITES, T1.ADDRESS ORDER BY SCORE DESC, FAVORITES DESC;